import React, { useState, useEffect } from 'react';
import { Search, Info, Calendar, ChevronDown, ChevronUp, ExternalLink } from 'lucide-react';
import Papa from 'papaparse';
import _ from 'lodash';

const App = () => {
  const [executiveOrders, setExecutiveOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: 'date', direction: 'desc' });
  const [dateRange, setDateRange] = useState({ startDate: '', endDate: '' });
  const [error, setError] = useState(null);

  // Fetch and parse CSV data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        setError(null);
        
        // Use fetch instead of window.fs.readFile for standard React apps
        const response = await fetch('/executive_orders_summarized.csv');
        
        if (!response.ok) {
          throw new Error(`Failed to fetch CSV: ${response.status} ${response.statusText}`);
        }
        
        const text = await response.text();
        
        // Log the first few characters to debug
        console.log("CSV data preview:", text.substring(0, 100));
        
        Papa.parse(text, {
          header: true,
          dynamicTyping: true,
          skipEmptyLines: true,
          complete: (results) => {
            if (results.errors && results.errors.length > 0) {
              console.warn("CSV parsing warnings:", results.errors);
            }
            
            // Log parsed data for debugging
            console.log("Parsed data sample:", results.data.slice(0, 2));
            console.log("Headers:", results.meta.fields);
            
            // Convert date strings to Date objects for sorting
            const processedData = results.data.map(order => {
              // Handle potential missing data
              if (!order.date) {
                console.warn("Order missing date:", order);
                return { ...order, parsedDate: new Date(0) }; // Default to epoch
              }
              
              try {
                // Parse date in MM/DD/YYYY format
                const dateParts = order.date.split('/');
                const parsedDate = new Date(
                  parseInt(dateParts[2]), // year
                  parseInt(dateParts[0]) - 1, // month (0-indexed)
                  parseInt(dateParts[1]) // day
                );
                
                return {
                  ...order,
                  parsedDate,
                };
              } catch (e) {
                console.error("Error parsing date for order:", order, e);
                return { ...order, parsedDate: new Date(0) }; // Default to epoch
              }
            });
            
            setExecutiveOrders(processedData);
            setFilteredOrders(processedData);
            setIsLoading(false);
          },
          error: (error) => {
            console.error('Error parsing CSV:', error);
            setError('Error parsing CSV file. Please check the file format.');
            setIsLoading(false);
          }
        });
      } catch (error) {
        console.error('Error fetching file:', error);
        setError('Error loading CSV file. Please check if the file exists and is accessible.');
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  // Filter orders based on search term and date range
  useEffect(() => {
    if (!executiveOrders.length) return;
    
    let filtered = [...executiveOrders];
    
    // Filter by search term
    if (searchTerm) {
      const lowercasedSearch = searchTerm.toLowerCase();
      filtered = filtered.filter(order => 
        (order.title && order.title.toLowerCase().includes(lowercasedSearch)) ||
        (order.content && order.content.toLowerCase().includes(lowercasedSearch)) ||
        (order.summary && order.summary.toLowerCase().includes(lowercasedSearch))
      );
    }
    
    // Filter by date range
    if (dateRange.startDate) {
      const startDate = new Date(dateRange.startDate);
      filtered = filtered.filter(order => order.parsedDate >= startDate);
    }
    
    if (dateRange.endDate) {
      const endDate = new Date(dateRange.endDate);
      filtered = filtered.filter(order => order.parsedDate <= endDate);
    }
    
    // Sort results
    filtered = _.orderBy(
      filtered,
      [sortConfig.key === 'date' ? 'parsedDate' : 'title'],
      [sortConfig.direction]
    );
    
    setFilteredOrders(filtered);
  }, [executiveOrders, searchTerm, dateRange, sortConfig]);

  // Handle sort toggle
  const handleSort = (key) => {
    setSortConfig(prevConfig => ({
      key,
      direction: prevConfig.key === key && prevConfig.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  // Handle order selection
  const handleOrderClick = (order) => {
    setSelectedOrder(prev => prev && prev.title === order.title ? null : order);
  };

  // Reset all filters
  const resetFilters = () => {
    setSearchTerm('');
    setDateRange({ startDate: '', endDate: '' });
    setSortConfig({ key: 'date', direction: 'desc' });
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-blue-900 text-white p-4 shadow-md">
        <div className="container mx-auto">
          <h1 className="text-2xl font-bold">Executive Orders Explorer</h1>
          <p className="text-blue-200">Browse and search U.S. Presidential Executive Orders with AI-generated summaries</p>
        </div>
      </header>
      
      {/* Main content */}
      <main className="container mx-auto flex-grow p-4">
        {/* Search and filters */}
        <div className="bg-white rounded-lg shadow-md p-4 mb-6">
          <div className="flex flex-col md:flex-row gap-4 mb-4">
            <div className="flex-grow relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Search size={20} className="text-gray-400" />
              </div>
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search executive orders by title, content, or summary..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            
            <div className="flex flex-col md:flex-row gap-2">
              <div className="flex items-center gap-2">
                <Calendar size={20} className="text-gray-400" />
                <input
                  type="date"
                  value={dateRange.startDate}
                  onChange={(e) => setDateRange(prev => ({ ...prev, startDate: e.target.value }))}
                  className="border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <span className="text-gray-500">to</span>
                <input
                  type="date"
                  value={dateRange.endDate}
                  onChange={(e) => setDateRange(prev => ({ ...prev, endDate: e.target.value }))}
                  className="border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              
              <button 
                onClick={resetFilters}
                className="bg-gray-200 hover:bg-gray-300 text-gray-700 font-medium px-4 py-2 rounded-md transition duration-150"
              >
                Reset
              </button>
            </div>
          </div>
          
          <div className="flex justify-between items-center text-sm text-gray-500">
            <div>
              {isLoading ? 'Loading...' : `Displaying ${filteredOrders.length} of ${executiveOrders.length} executive orders`}
            </div>
            <div className="flex gap-4">
              <button 
                onClick={() => handleSort('title')}
                className="flex items-center gap-1 hover:text-blue-700"
              >
                Sort by Title
                {sortConfig.key === 'title' && (
                  sortConfig.direction === 'asc' ? 
                  <ChevronUp size={16} /> : 
                  <ChevronDown size={16} />
                )}
              </button>
              <button 
                onClick={() => handleSort('date')}
                className="flex items-center gap-1 hover:text-blue-700"
              >
                Sort by Date
                {sortConfig.key === 'date' && (
                  sortConfig.direction === 'asc' ? 
                  <ChevronUp size={16} /> : 
                  <ChevronDown size={16} />
                )}
              </button>
            </div>
          </div>
        </div>
        
        {/* Loading state */}
        {isLoading && (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        )}
        
        {/* Error state */}
        {error && (
          <div className="text-center py-16 bg-white rounded-lg shadow-md">
            <Info size={48} className="mx-auto text-red-500 mb-4" />
            <h2 className="text-xl font-semibold text-gray-700 mb-2">Error Loading Data</h2>
            <p className="text-gray-500">{error}</p>
            <div className="mt-4 p-4 bg-gray-100 rounded text-left">
              <p className="font-medium">Troubleshooting tips:</p>
              <ul className="list-disc pl-5 mt-2 text-gray-700">
                <li>Make sure the CSV file is in the public folder</li>
                <li>Verify the file name is exactly: "executive_orders_summarized_20250401 sample.csv"</li>
                <li>Check that the CSV has headers: title, date, content, summary, link, page_number</li>
                <li>Ensure dates are in MM/DD/YYYY format</li>
              </ul>
            </div>
          </div>
        )}
        
        {/* No results state */}
        {!isLoading && !error && filteredOrders.length === 0 && (
          <div className="text-center py-16 bg-white rounded-lg shadow-md">
            <Info size={48} className="mx-auto text-gray-400 mb-4" />
            <h2 className="text-xl font-semibold text-gray-700 mb-2">No executive orders found</h2>
            <p className="text-gray-500">Try adjusting your search criteria or filters</p>
          </div>
        )}
        
        {/* Results list */}
        {!isLoading && !error && filteredOrders.length > 0 && (
          <div className="space-y-4">
            {filteredOrders.map((order, index) => (
              <div 
                key={index} 
                className="bg-white rounded-lg shadow-md overflow-hidden cursor-pointer"
              >
                <div 
                  onClick={() => handleOrderClick(order)}
                  className="p-4 hover:bg-gray-50 transition-colors duration-150"
                >
                  <div className="flex justify-between items-start">
                    <h2 className="text-lg font-semibold text-gray-800 pr-4">{order.title}</h2>
                    <div className="flex items-center text-sm text-gray-500 whitespace-nowrap">
                      <Calendar size={16} className="mr-1" />
                      {order.date}
                    </div>
                  </div>
                  
                  {selectedOrder && selectedOrder.title === order.title && (
                    <div className="mt-4 border-t pt-4">
                      <div className="prose max-w-none">
                        {/* Format summary as markdown with line breaks */}
                        {order.summary && order.summary.split('\n').map((line, i) => (
                          <React.Fragment key={i}>
                            {line}
                            <br />
                          </React.Fragment>
                        ))}
                      </div>
                      
                      <div className="mt-4 pt-3 border-t flex justify-between items-center">
                        <a 
                          href={order.link} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="inline-flex items-center text-blue-600 hover:text-blue-800"
                        >
                          View Original <ExternalLink size={16} className="ml-1" />
                        </a>
                        <span className="text-sm text-gray-500">
                          Page {order.page_number}
                        </span>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
      
      {/* Footer */}
      <footer className="bg-gray-100 border-t border-gray-200 mt-8 py-4">
        <div className="container mx-auto px-4 text-center text-gray-600 text-sm">
          <p>Executive Orders data last updated: April 1, 2025</p>
          <p className="mt-2">This tool is for informational purposes only.</p>
        </div>
      </footer>
    </div>
  );
};

export default App;
